  `email` varchar(64) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `contact` varchar(64) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `logo` varchar(64) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
